let infoObject;const currentPath=document.location.pathname;let currentLang=localStorage.getItem("language")||"ru";const langButtons=document.querySelectorAll("[data-switch]");async function checkPageAdress(){switch(currentPath){case"/index.html":infoObject=await import("/js/languages/index_translate.js");break;case"/services.html":infoObject=await import("/js/languages/services_translate.js");break;case"/import.html":infoObject=await import("/js/languages/import_translate.js");break;case"/export.html":infoObject=await import("/js/languages/export_translate.js");break;case"/providers.html":infoObject=await import("/js/languages/providers_translate.js");break;case"/partners.html":infoObject=await import("/js/languages/partners_translate.js");break;case"/product_page.html":case"/trends_page.html":infoObject=await import("/js/languages/product_page_translate.js");break;default:infoObject=await import("/js/languages/index_translate.js")}}function checkBtnActive(){switch(currentLang){case"ru":document.querySelector('[data-switch="ru"]').classList.add("language-active");break;case"cn":document.querySelector('[data-switch="cn"]').classList.add("language-active");break;default:document.querySelector('[data-switch="ru"]').classList.add("language-active")}}function changeLang(){for(let t in infoObject.translationList)document.querySelectorAll(`[data-lang="${t}"]`).forEach(a=>{a.textContent=infoObject.translationList[t][currentLang]})}checkPageAdress(),langButtons.forEach(a=>{a.addEventListener("click",a=>{currentLang=a.currentTarget.dataset.switch,localStorage.setItem("language",a.currentTarget.dataset.switch),langButtons.forEach(a=>{a.classList.remove("language-active")}),a.currentTarget.classList.add("language-active"),changeLang()})}),checkBtnActive(),changeLang();